// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title ReentrancyAttacker
 * @notice Contrat malveillant pour tester la protection contre la réentrance
 * @dev Utilisé uniquement pour les tests de sécurité
 */

interface ISafeClub {
    function executeProposal(uint256 _proposalId) external;
}

contract ReentrancyAttacker {
    ISafeClub public target;
    uint256 public attackCount;
    uint256 public maxAttacks = 3;
    bool public attackEnabled = true;
    
    event AttackAttempted(uint256 count);
    event AttackFailed(string reason);
    
    constructor(address _target) {
        target = ISafeClub(_target);
    }
    
    /**
     * @notice Fonction receive qui tente la réentrance
     * @dev Appelée quand le contrat reçoit des ETH
     */
    receive() external payable {
        if (attackEnabled && attackCount < maxAttacks) {
            attackCount++;
            emit AttackAttempted(attackCount);
            
            try target.executeProposal(0) {
                // Si cette ligne s'exécute, la réentrance a réussi
                // Dans un vrai contrat sécurisé, on ne devrait jamais arriver ici
            } catch Error(string memory reason) {
                emit AttackFailed(reason);
            } catch {
                emit AttackFailed("Unknown error");
            }
        }
    }
    
    /**
     * @notice Permet de retirer les fonds du contrat (pour les tests)
     */
    function withdraw() external {
        payable(msg.sender).transfer(address(this).balance);
    }
    
    /**
     * @notice Active/désactive l'attaque
     */
    function setAttackEnabled(bool _enabled) external {
        attackEnabled = _enabled;
    }
    
    /**
     * @notice Reset le compteur d'attaques
     */
    function resetAttackCount() external {
        attackCount = 0;
    }
}

